import React,{Component} from "react";
import AppNavbar from './AppNavBar'
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import {Redirect} from "react-router-dom";
class NewPost extends Component{

    post={
        user:'',
        text:'',
        redirectToList:false
    }


    constructor(props) {
        super(props);
        this.state = {post: this.post};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let post = {...this.state.post};
        post[name] = value;
        this.setState({post});
    }

    handleSubmit(event) {
        event.preventDefault();
        const {post} = this.state;

         fetch('/post?user='+post.user+'&text='+post.text, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
        }).catch(reason => console.log(reason));
        this.setState(()=> ({redirectToList: true}))
    }

    render() {
        const {post} = this.state
        const{redirectToList} =this.state
        if(redirectToList === true){
            return <Redirect to='/posts'/>
        }

        return  <div>
            <AppNavbar/>
            <Container>
                Adicionar Post
                <Form onSubmit={this.handleSubmit}>
                    <div>
                    <FormGroup>
                        <Label for="user">User</Label>
                        <Input type="text" name="user" id="user" value={post.user || ''}
                               onChange={this.handleChange}/>
                    </FormGroup>
                    <FormGroup>
                        <Label for="text">Text</Label>
                        <Input type="text" name="text" id="text" value={post.text || ''}
                               onChange={this.handleChange}/>
                    </FormGroup>
                    </div>
                    <FormGroup>
                        <Button color="primary" type="submit">Salvar</Button>{' '}
                    </FormGroup>
                </Form>
            </Container>
        </div>;
    }
} export default NewPost;
