import React, {Component} from 'react'
import AppNavbar from "./AppNavBar";
class Posts extends Component{


    constructor(props) {
        super(props);
        this.state = {posts: [], isLoading: true};
        this.upvote = this.upvote.bind(this)
    }

    componentDidMount() {
        this.setState({isLoading:true});
        fetch('/posts')
            .then( res => res.json())
            .then((data) => {
                this.setState({posts: data, isLoading: false})
            })
            .catch(console.log)
    }

    upvote(id){
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json'},
        };
        fetch('/upvote?id='+id,requestOptions)
            .catch(reason => console.log(reason))
            .then(()=> {
                let json = this.state.posts
                for( var k = 0; k < json.length; ++k ) {
                    if( id === json[k]['id'] ) {
                        json[k]['upvotes'] = json[k]['upvotes']+1 ;
                    }
                }
                this.setState({posts:json})
            })

}
render() {
    const {posts, isLoading} = this.state;

    if (isLoading) {
        return  '<AppNavbar/>  <p>Loading...</p>';
    }else
            { return  <AppNavbar/>
                posts.map((post) => (
                <div class="card">
                    <div class="card-body">
                        <h5 class="card-title">{post.user}</h5>
                        <h6 class="card-subtitle mb-2 text-muted">{post.date}</h6>
                        <p class="card-text">{post.text}</p>
                        <h5>Upvotes: {post.upvotes}</h5>
                        <button size="sm" color="danger" onClick={() => this.upvote(post.id)}>Upvote</button>
                    </div>
                </div>
            ))}


}
} export default Posts;
