import React, {Component} from 'react';
import Posts from './components/posts';

class App extends Component {
    render () {
    return (
            Posts(this.props,this.context,this.state).render()
    );
  }

    state = {
        posts : []
    };
    componentDidMount() {
        fetch('/posts')
            .then( res => res.json())
            .then((data) => {
                this.setState({posts: data})
            })
            .catch(console.log)
    }
}

export default App;
